import { connect } from 'react-redux'
import Form from '../components/Form'
import storeAllLocations from '../actions/locationActions'
import fetchAllLocations from '../actions/locationActions'

const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {
    saveLocation: (location) => {
      dispatch({
        type: 'SAVE_LOCATION',
        data: location,
        action: //post data to server, response messages in case of validation error or server communication success/failure
        fetch("http://localhost:3000/locations", {
          method: "post",
          headers: {"Content-Type": "application/json"},
          body: JSON.stringify(location)
        })
          .then(storeAllLocations())
          .then(fetchAllLocations())
          .then(response => response.json("Location Sent"))
          .catch(response => response.json("There was a problem sending the location to the server"))
      })
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Form);
