import React, { Component } from 'react';

class Form extends Component {
  state = {
    errorMessage: ""
  }

  submitForm(e, data) {
    if (Math.abs(data.lat) <= 90 && Math.abs(data.lng) <= 180) {
      e.preventDefault();
      this.props.saveLocation(data);
      fetch("http://localhost:3000/locations", {
        method: "post",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(data)
      })
        .then(response => response.json("Location Sent"))
    }
    else{
      this.setState({errorMessage : "Your latitude or longitude are invalid, please check the coordinates and try again"})
    }
  }

  render() {
    return (
      <form className="form">
        <label>
          Name
          <input
            ref={(input) => { this.name = input }}
            type="text"
          />
        </label>
        <label>
          Lat
          <input
            ref={(input) => { this.lat = input }}
            type="text"
          />
        </label>
        <label>
          Long
          <input
            ref={(input) => { this.lng = input }}
            type="text"
            />
        </label>
        <button
          type="submit"
          onClick={(e) => this.submitForm(e, {
            name: this.name.value,
            lat: this.lat.value,
            lng: this.lng.value
          })}
        >
            Save
        </button>
      </form>
    );
  }
}


export default Form;
