const initialState = {
    data: [],
    locationCounter : 3
}

const saveInput = (state = initialState, action) => {
    switch (action.type) {
      case 'SAVE_LOCATION':
        state.locationCounter ++;
        return  fetch("http://localhost:3000/locations", {
          method: "post",
          headers: {"Content-Type": "application/json"},
          body: JSON.stringify(action.data)
        })
      default:
          return state;      
    }
  }

  export default saveInput;
