import React, { Component } from 'react';

class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name : "",
      lat : "",
      lng : ""
    }
  }
  
  saveLocation(data) {
    const sendLocation = {
      method: 'POST',
      headers: {'Content-Type' : 'application/json'},
      body: JSON.stringify({name: this.state.name, lat: this.state.lat, lng: this.state.lng})
    };
    fetch('localhost:3001', sendLocation)
      .then(response => response.json())
      .then(data => this.setState({name: "", lat: "", lng: ""}));
  }

  submitForm(e, data) {
    e.preventDefault();
    this.props.saveLocation(data);
  }

  render() {
    return (
      <form className="form">
        <label>
          Name
          <input
            ref={(input) => { this.name = input }}
            type="text"
          />
        </label>
        <label>
          Lat
          <input
            ref={(input) => { this.lat = input }}
            type="text"
          />
        </label>
        <label>
          Long
          <input
            ref={(input) => { this.lng = input }}
            type="text"/>
        </label>
        <button
          type="submit"
          onClick={(e) => this.submitForm(e, {
            name: this.state.name.value,
            lat: this.state.lat.value,
            lng: this.state.lng.value
          })}
        >
            Save
        </button>
      </form>
    );
  }
}


export default Form;
